#include <iostream>
#include <string>
#include <vector>

using namespace std;

/*
 * Complete the 'superDigit' function below.
 *
 * The function is expected to return an INTEGER.
 * The function accepts following parameters:
 *  1. STRING n
 *  2. INTEGER k
 */
// Recursive helper function to calculate the super digit of a string of digits
void addDigits(string &digits)
{
        long sum = 0;
        if (digits.length() == 1)
        {
                cout << "The super digit is " << digits << endl;
                return;
        }

        for (char &c : digits)
        {
                sum += c - '0';
        }

        digits = to_string(sum);
        addDigits(digits);
}

int superDigit(string n, int k)
{
        long initialSum = 0;

        // Calculate the sum of digits of the string `n`
        for (char &c : n)
        {
                initialSum += c - '0';
        }

        // Multiply by `k` to get the starting point for the super digit calculation
        initialSum *= k;

        // Convert the sum back to a string for processing in `addDigits`
        string fullDigits = to_string(initialSum);
        addDigits(fullDigits);

        // Convert the result back to an integer
        return stoi(fullDigits);
}

int main()
{
        string numStr = "";
        int k = 100000;
        superDigit(numStr, k);
}